mapper.update:
UPDATE colaborador
SET foto = #{foto}
WHERE idColaborador = #{idColaborador}

mapper.select:
SELECT idColaborador, nombre, apellidoPaterno, apellidoMaterno, TO_BASE64(foto) AS fotoBase64
FROM colaborador
WHERE idColaborador = #{idColaborador};

SELECT idColaborador,nombre, apellidoPaterno
FROM colaborador
WHERE idRol = #{idRol}
